package intCompare;

public class IntCompare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 100;
				
		if (x == 10) {
			
			System.out.println("Compared " + x + " with 10");
		}
		else if (x == 50) {
			System.out.println("Compared " + x + " with 50");
		}
		
		else if (x == 200) {
			System.out.println("Compared " + x + " with 200");
		}
		
		else if (x == 100) {
			System.out.println("Compared " + x + " with 100");
		}
		
		else {
			
			System.out.println("Compared " + x + " with nothing");
		}

	}

}
